mapper.select:
SELECT COUNT(*)
FROM tb_dashboard_permission
WHERE dashboard_id = #{dashboardId}
  AND target_type = 'USER'
  AND target_id = #{userId}
  AND permission_level = #{permissionLevel}
  AND deleted = 0

SELECT COUNT(*)
FROM tb_dashboard_permission
WHERE dashboard_id = #{dashboardId}
  AND target_type = 'ROLE'
  AND target_id = #{roleId}
  AND permission_level = #{permissionLevel}
  AND deleted = 0

